                      --       'J',
                      --       f_abrevia_nome(p.nopessoa, 20, 'PEA'),
                      --       null) nome_internacional,
                      f_abrevia_nome(nvl(p.noabreviado,p.nopessoa), 20, 'PEA') nome_internacional,
                      null unidade_grupo,
                      null grupo,
                      null compl_grupo,
                      null local_trabalho,
                      null end_com,
