      --DBMS_OUTPUT.PUT_LINE('P500.1 REGISTRO/CPF/CPF COM MASCARA: ' || rbeneficiario.nrsequencial_usuario ||
      --'/' || rbeneficiario.nrcpf || '/' || lpad(trim(rbeneficiario.nrcpf), 11, '0') );


