           x.num_mes_ult_faturam, --num_mes_ult_faturam ,
           x.cd_motivo_cancel, --cdn_motiv_cancel ,
           gp.import_modul_bnfciar_seq.currval, /*,progress_recid*/
           x.dtfinal_carencia,
           x.num_seqcial_control);

        p_commit;

      exception
        when others then
          null;
      end;
      --  end if;
    --   end loop;

    end loop;
    --commit;
  end p_grava_modulo_benef_r2;

