               and aa.progress_recid = a.progress_recid;
            commit;

            w := w + 1;

            if w > pqtsessoes_beneficiario then
              w := 0;
            end if;

          end if;

        end if;

      end loop;

    end loop;
  end p_atualiza_status_benef;

