                               nvl(pcp.cdlotacao, '*') cdlotacao_preco,
                               nvl(pcp.cdusucateg, '*') cdusucateg_preco,
                               nvl(pcp.tpdependente, '*') tpdependente_preco,
                               nvl(max(pcp.nrperiodo_ult_reajuste), 0) nrperiodo_ult_reajuste_preco
