    procedure check_criterio_migrado(pout_criterio_migrado out varchar2,
                                     pregra                number,
                                     pcd_modulo            varchar2,
                                     pcd_grau_parentesco   varchar2,
                                     pnridade_inicio       number,
                                     pnridade_fim          number,
                                     pvl_mensalidade_base  number) is
    begin
      select 'S'
