          --se nao houver próximo reajuste irá mover 999912 então o dia anterior será 30/11/9999
          select (to_date('01/' || substr(periodo_fim, 5, 2) || '/' ||
                          substr(periodo_fim, 1, 4),
                          'DD/MM/YYYY') - 1)
            into vddtfim_reajuste
