                    end*/

               when a.NRAREA_ACAO <> pnrunimed and
                    a.cdarea_acao_prestador = pnrunimed and
                    e.cdserie_guia is not null
                    and s.nrautorizacao = s.nrseq_complemento then --eh a guia principal
                          case
