    --DBMS_OUTPUT.PUT_LINE('NRAUTORIZACAO/NRSEQ_COMPLEMENTO: ' || a.nrautorizacao || '/' || a.nrseq_complemento);

          -- se já existir IMPORT_GUIA com a mesma chave, significa que está adicionando mais serviços à mesma guia.
