                  begin
                    select re.cdvalor_externo
                      into vnrglosa
                      from mig_tab_conversao_exp re, mig_tab_conversao r
                     where re.nrseq_tab_conversao = r.nrseq
                       and r.notabela = 'MIGRACAO_GLOSA'
                       and re.cdvalor_interno = oa.cdocorrencia;

