           progress_recid,
           char_1,
           char_2,
           dec_1,
           dec_2,
           dec_3,
           dec_4,
           dec_5,
           int_1,
           int_2,
           int_3,
           int_4,
           int_5,
           u_char_1,
           u_char_2,
           u_char_3,
           u_dec_1,
           u_dec_2,
           u_dec_3,
           u_int_1,
           u_int_2,
           u_int_3,
           nm_conjuge,
           dt_atualizacao,
           id_pessoa_crm,
           ds_integracao_crm,
           int_6,
           int_7,
           int_8,
           int_9,
           int_10,
           int_11,
           int_12,
           int_13,
           int_14,
           int_15,
           dec_6,
           dec_7,
           dec_8,
           dec_9,
           dec_10,
           char_6,
           char_7,
           char_8,
           char_9,
           char_10,
           char_11,
           char_12,
