                    and pdr.cdcontrato = tpc.cdcontrato) loop

        begin
          --
          select 'S'
            into vaomigrado
            from gp.reg_plano_saude rps
           where rps.cdn_plano_ans          = cp.cdn_plano_ans
             and rps.cod_plano_operadora    = cp.cod_plano_operadora
