      select distinct rem.cd_modalidade cd_modalidade,
                      rem.cd_plano cd_plano,
                      rem.cd_tipo_plano cd_tipo_plano,
                      nvl(rmo.cdvalor_externo, cs.cdcategserv) cd_modulo,
                      rcs.cdvalor_externo cd_tipo_insumo,
