          tcondsaud.int_3                := 0;
          tcondsaud.int_4                := 0;
          tcondsaud.int_5                := 0;
          tcondsaud.int_1                := 0;
          tcondsaud.int_2                := 0;
          tcondsaud.u_char_3             := ' ';
          tcondsaud.u_date_1             := '';
          tcondsaud.u_date_2             := '';
          tcondsaud.log_1                := 0;
          tcondsaud.log_2                := 0;
          tcondsaud.log_3                := 0;
          tcondsaud.log_4                := 0;
