      ttipomens.u_date_1              := '';
      ttipomens.u_date_2              := '';
      ttipomens.u_date_3              := '';
      ttipomens.u_dec_1               := 0;
      ttipomens.u_dec_2               := 0;
      ttipomens.u_dec_3               := 0;
      ttipomens.u_log_1               := 0;
      ttipomens.u_log_2               := 0;
      ttipomens.u_log_3               := 0;
      ttipomens.char_4                := ' ';
      ttipomens.char_5                := ' ';
      ttipomens.date_4                := '';
      ttipomens.date_5                := '';
      ttipomens.dec_5                 := 0;
      ttipomens.int_4                 := 0;
      ttipomens.int_5                 := 0;
      ttipomens.log_4                 := 0;
      ttipomens.log_5                 := 0;
      ttipomens.dec_4                 := 0;
      ttipomens.char_1                := ' ';
      ttipomens.int_1                 := 0;
      ttipomens.u##ind_tip_comb := case
                                     when m.nr_proposta = 0 then
                                      'P'
                                     else
                                      'C'
                                   end;
      ttipomens.ind_tip_comb := case
                                  when m.nr_proposta = 0 then
                                   'P'
                                  else
