         'CBO2002',
         'Rever cadastro de Sub Grupos CBO',
         cUSER_MIGRACAO,
         dzsubcbo_seq.nextval);

      --vQtRegistro4 := vQtRegistro4 + 1;
    end if;

    -- Cadastrar o CBO --
    begin
      begin
        select 'S'
          into vAoMigrado
          from dz_cbo02
         where cd_cbo = rcb.cdcbo_atual;
      exception
        when no_data_found then
          vAoMigrado := 'N';

      end;

      if vAoMigrado = 'N' then
        insert into dz_cbo02
          (cd_cbo,
           in_sigla_cbo,
           u##ds_cbo,
           ds_cbo,
           in_tipo_cbo,
           cd_cbo_interno,
           cd_familia_cbo,
           cd_grupo_cbo,
           cd_sub_grupo_cbo,
           cd_userid,
           progress_recid)
        values
