           upper(rcb.nocbo),
           rcb.nocbo,
           'CBO2002',
           rcb.cdcbo_atual,
           substr(rcb.cdcbo_atual, 1, 4),
           substr(rcb.cdcbo_atual, 1, 1),
           substr(rcb.cdcbo_atual, 2, 3),
           cUSER_MIGRACAO,
           dz_cbo02_seq.nextval);

        vQtRegistro1 := vQtRegistro1 + 1;

      end if;
    end;

    countOfRecords := countOfRecords + 1;

    if (countOfRecords >= 1000) then
      commit;
      countOfRecords := 0;
    end if;

  end loop;
end p_migra_cbo;
