CREATE OR REPLACE VIEW UNICOOGPS.V_ANALISA_END_PREST AS
SELECT P.CD_UNIDADE,
       P.CD_PRESTADOR,
       E.NOLOGRADOURO || ', ' || E.NRIMOVEL EN_ENDERECO,
       E.NOBAIRRO EN_BAIRRO,
       (SELECT RE.CDVALOR_EXTERNO
          FROM REM_TAB_CONVERSAO R, REM_TAB_CONVERSAO_EXP RE
         WHERE R.NRSEQ = RE.NRSEQ_TAB_CONVERSAO
           AND R.NOTABELA = 'MIGRACAO_CIDADE'
           AND RE.CDVALOR_INTERNO = E.CDCIDADE) CD_CIDADE,
       E.NRCEP EN_CEP,
       E.CDESTADO EN_UF,
       NVL('(' || E.NRDDD || ') ' || E.NRTELEFONE, ' ') NR_FONE##1,
       NVL(E.NRCELULAR, ' ') NR_FONE##2,
       DECODE(E.AOCORRESPONDENCIA, 'S', 1, 0) LG_MALOTE,
       DECODE(E.AOCORRESPONDENCIA, 'S', 1, 0) LG_RECEBE_CORRESP,
       NVL(E.CDEMAIL, ' ') CHAR_2,
       NVL(E.TXCOMPLEMENTO, ' ') CHAR_3,
       E.NRREGISTRO ID_ENDERECO,
       (SELECT RE.CDVALOR_EXTERNO
          FROM REM_TAB_CONVERSAO R, REM_TAB_CONVERSAO_EXP RE
         WHERE R.NRSEQ = RE.NRSEQ_TAB_CONVERSAO
           AND R.NOTABELA = 'MIGRACAO_TIPO_ENDERECO'
           AND RE.CDVALOR_INTERNO = E.Tpendereco) dec_2
  FROM PRESTADOR PR, GP.PRESERV P, ENDERECO E
 WHERE PR.CDPRESTADOR = P.CD_PRESTADOR
   AND P.CD_UNIDADE = F_PARAMETRO('NRUNIMED')
   AND PR.NRREGISTRO_PREST = E.NRREGISTRO
 ORDER BY DECODE(E.AOCORRESPONDENCIA, 'S', 1, 2)
