create or replace view unicoogps.v_cpt_unicoo as
select nrsequencial_usuario, cddoenca , dtfinal_carencia,cdservico , qtdias_carencia
  from (select distinct t.nrsequencial_usuario,
                        t.cddoenca,
                        t.dtfinal_carencia,
                        v2.cdservico,
                       dtfinal_carencia - dtinicio_doenca qtdias_carencia
          from doenca_usuario              t,
               servico_doenca_usuario      s,
               v_servico_doenca_controlada v1,
               v_servico_doenca_controlada v2
         where t.nrsequencial_usuario = s.nrsequencial_usuario(+)
           and t.nrsequencial_usuario = s.cddoenca(+)
           and s.nrseq_serv_doenca = v1.nrseq(+)
           and t.cddoenca = v2.cddoenca
        union
        select distinct t.nrsequencial_usuario,
                        t.cddoenca,
                        t.dtfinal_carencia,
                        v1.cdservico,
                        dtfinal_carencia - dtinicio_doenca qtdias_carencia
