prompt Creating view V_PRESTADOR_ESTABELECIMENTO
prompt =========================================
prompt
create or replace view unicoogps.v_prestador_estabelecimento as
select pr."NRREGISTRO_PREST",pr."CDTIPO_ENDERECO",pr."NRCNES",pr."NRLEITOS_TOTAIS",pr."NRLEITOS_CONTRATADOS",pr."NRLEITOS_PSIQUIATRIA",pr."NRLEITOS_UTI_ADULTO",pr."NRLEITOS_UTI_NEONATAL",pr."NRLEITOS_UTI_PEDIATR",pr."NRLEITOS_INTERMED_NEONATAL",pr."IDFILIAL",pr."NRCNPJ_FILIAL",pr."NRLEITOS_HOSPITAL_DIA",pr."NRLEITOS_TOT_CLINIC",pr."NRLEITOS_TOT_CIRUR",pr."NRLEITOS_TOT_OBSTR",pr."NRLEITOS_TOT_PEDIAT",pr."NRLEITOS_TOT_PSIQU",pr."NRLATITUDE",pr."NRLONGITUDE",pr."NRLEITOS_INTERMED"
  from prestador_estabelecimento pr, prestador p, endereco e, endpres ed
 where p.nrregistro_prest = pr.nrregistro_prest
   and e.nrregistro = pr.nrregistro_prest
   and e.tpendereco = pr.cdtipo_endereco
   and ed.cd_prestador = p.cdprestador
   and ed.u##en_endereco = case
       --logradouro + nrimovel + txcomplemento <= 40
         when length(e.nologradouro ||
                     decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
                     decode(e.txcomplemento,
                            null,
                            null,
                            ' ' || e.txcomplemento)) <= 40 then
          e.nologradouro ||
          decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
          decode(e.txcomplemento, null, null, ' ' || e.txcomplemento)
       --logradouro reduz + nrimovel + txcomplemento <=40
         when length(ems506unicoo.f_reduz_end(nologradouro, 0) ||
                     decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
                     decode(ems506unicoo.f_reduz_compl(e.txcomplemento, 0),
                            null,
                            null,
                            ' ' || e.txcomplemento)) <= 40 then
          ems506unicoo.f_reduz_end(e.nologradouro, 0) ||
          decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
          decode(e.txcomplemento, null, null, ' ' || e.txcomplemento)
       --logradouro reduz + nrimovel + txcomplemento reduz <=40
         when length(ems506unicoo.f_reduz_end(e.nologradouro, 0) ||
                     decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
                     decode(ems506unicoo.f_reduz_compl(e.txcomplemento, 0),
                            null,
                            null,
                            ' ' || e.txcomplemento)) <= 40 then
          ems506unicoo.f_reduz_end(e.nologradouro, 0) ||
          decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
          decode(ems506unicoo.f_reduz_compl(e.txcomplemento, 0),
                 null,
                 null,
                 ' ' || e.txcomplemento)
       -- logradouro reduz abrev + nrimovel + complemento reduz
         when length(ems506unicoo.f_reduz_end_abrev(ems506unicoo.f_reduz_end(e.nologradouro,
                                                                             0),
                                                    0) ||
                     decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
                     decode(ems506unicoo.f_reduz_compl(e.txcomplemento, 0),
                            null,
                            null,
                            ' ' || e.txcomplemento)) <= 40 then
          ems506unicoo.f_reduz_end_abrev(ems506unicoo.f_reduz_end(e.nologradouro,
                                                                  0),
                                         0) ||
          decode(e.nrimovel, null, null, ' ' || e.nrimovel) ||
          decode(ems506unicoo.f_reduz_compl(txcomplemento, 0),
                 null,
                 null,
                 ' ' || e.txcomplemento)

       end
/

