prompt
CREATE OR REPLACE VIEW UNICOOGPS.V_USUARIOS_MIGRADOS AS
SELECT UNICOO."NRREGISTRO",UNICOO."NRCONTRATO",UNICOO."QTDE_USUARIOS_UNICOO",
       GP.QTDE_MIGRADO,
       TRUNC((GP.QTDE_MIGRADO * 100) / UNICOO.QTDE_USUARIOS_UNICOO) PORCENTAGEM_MIGRADO,
       DECODE((SELECT COUNT(*)
          FROM USUARIO U, FAMILIA F
         WHERE U.NRREGISTRO = UNICOO.NRREGISTRO
           AND U.NRCONTRATO = UNICOO.NRCONTRATO
           AND U.NRREGISTRO = F.NRREGISTRO
           AND U.NRCONTRATO = F.NRCONTRATO
           AND U.NRFAMILIA = F.NRFAMILIA
           AND (GREATEST(NVL(F.DTFINAL_CONTRATO,U.DTEXCLUSAO), U.DTEXCLUSAO) IS NULL OR
               GREATEST(NVL(F.DTFINAL_CONTRATO,U.DTEXCLUSAO), U.DTEXCLUSAO) > trunc(sysdate))),0,'NAO','SIM') POSSUI_ATIVOS,
         DECODE( (SELECT COUNT(*) FROM IMPORT_PROPOST PR, IMPORT_BNFCIAR IB
          WHERE PR.NR_CONTRATO_ANTIGO = IB.NR_CONTRATO_ANTIGO
          AND PR.NUM_LIVRE_2 = GP.NRREGISTRO
          AND PR.NUM_LIVRE_3  = GP.NRCONTRATO
          AND IB.IND_SIT_IMPORT = 'ER'),0, 'NAO','SIM') BENEF_ERRO_MIGRACAO

  FROM (SELECT NRREGISTRO, NRCONTRATO, COUNT(*) QTDE_USUARIOS_UNICOO
          FROM USUARIO U
         WHERE U.NRFAMILIA <> 0
         GROUP BY NRREGISTRO, NRCONTRATO) UNICOO,
       (SELECT P.NUM_LIVRE_2 NRREGISTRO,
               P.NUM_LIVRE_3 NRCONTRATO,
               COUNT(*) QTDE_MIGRADO
          FROM IMPORT_BNFCIAR IB, IMPORT_PROPOST P, GP.USUARIO U
         WHERE IB.CD_CARTEIRA_ANTIGA = U.CD_CARTEIRA_ANTIGA
           AND IB.NR_CONTRATO_ANTIGO = P.NR_CONTRATO_ANTIGO
         GROUP BY P.NUM_LIVRE_2, P.NUM_LIVRE_3) GP
 WHERE UNICOO.NRREGISTRO = GP.NRREGISTRO
   AND UNICOO.NRCONTRATO = GP.NRCONTRATO
