CREATE OR REPLACE FUNCTION UNICOOGPS."F_LOTAC" (PNRCONTRATO IN USUARIO.NRCONTRATO%TYPE) return CHAR is
  VAOLOTACNAO CHAR;
begin

BEGIN
      SELECT 'S'
        INTO VAOLOTACNAO
        FROM CONTRATO_DA_PESSOA CDP
       WHERE TPFATURA = 'U'
         AND EXISTS (SELECT 1
                FROM LOTACAO L
               WHERE CDP.NRREGISTRO = L.NRREGISTRO
                 AND CDP.NRCONTRATO = L.NRCONTRATO)
                 AND CDP.NRCONTRATO = PNRCONTRATO;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        VAOLOTACNAO := 'N';
    END;

  return(VAOLOTACNAO);
END F_LOTAC ;
/
