CREATE OR REPLACE PROCEDURE UNICOOGPS."P_ANALISE_MODULOS_DEPARA" is
  vaoexiste char;
  vcont     number := 0;

  cursor c_planos is
  
    select /*+ rule +*/
     t.cdcontrato,
     t.cdcontrato_agr,
     d.cdmodulo,
     td.cd_modalidade,
     td.cd_plano,
     td.cd_tipo_plano,
     d.cdcategserv
      from temp_depara_detalhado t,
           categoria_de_servico  c,
           depara_modulo         d,
           temp_depara_agrupado  td
     where t.cdcontrato = c.cdcontrato
       and d.cdcategserv = c.cdcategserv
       and td.cdcontrato = t.cdcontrato_agr;

begin

  delete temp_modulos;
  commit;

  for c in c_planos loop
  
    begin
      select 'S'
        into vaoexiste
        from temp_depara_agrupado t1,
             categoria_de_servico c1,
             depara_modulo        d1
       where t1.cdcontrato = c1.cdcontrato
         and d1.cdcategserv = c1.cdcategserv
         and t1.cdcontrato = c.cdcontrato_agr
         and d1.cdmodulo = c.cdmodulo
         and rownum = 1;
    exception
      when no_data_found then
        insert into temp_modulos
        values
          (c.cdcontrato,
           c.cd_modalidade,
           c.cd_plano,
           c.cd_tipo_plano,
           c.cdmodulo,
           c.cdcategserv,
           c.cdcontrato_agr,
           'Modulo do plano original no est associado a nenhum mdulo ao plano modelo no de/para');
      
        vcont := vcont + 1;
      
        if vcont > 1000 then
          commit;
          vcont := 0;
        end if;
      
    end;
  
  end loop;

  commit;

end p_analise_modulos_depara;
