prompt
CREATE OR REPLACE PROCEDURE UNICOOGPS."P_APAGA_GUIAS" is

  vcont     number := 0;
  vseq_guia number;

  cursor c_guias is
  
    select ig.num_seqcial, ig.ind_tip_guia
      from import_guia ig
     where ig.u##ind_sit_import = 'AP';

  cursor c_movimentos(pval_seq_guia import_guia_movto.val_seq_guia%type) is
    select igm.progress_recid
      from import_guia_movto igm
     where igm.val_seq_guia = pval_seq_guia;

begin
  for cg in c_guias loop
  
    if cg.ind_tip_guia = 'I' then
    
      begin
        select t.num_seqcial_guia
          into vseq_guia
          from import_guia_intrcao t
         where t.num_seq_import = cg.num_seqcial;
      
        delete from import_guia_intrcao t
         where t.num_seq_import = cg.num_seqcial;
      exception
        when others then
          null;
      end;
      vcont := vcont + 1;
    
    elsif cg.ind_tip_guia = 'C' then
      begin
        select ic.num_seqcial_guia
          into vseq_guia
          from import_guia_con ic
         where ic.num_seq_import = cg.num_seqcial;
      
        delete from import_guia_con ic
         where ic.num_seq_import = cg.num_seqcial;
      exception
        when others then
          null;
      end;
      vcont := vcont + 1;
    
    elsif cg.ind_tip_guia = 'S' then
    
      begin
        select igs.val_seqcial
          into vseq_guia
          from import_guia_sadt igs
         where igs.val_seq_import = cg.num_seqcial;
      
        delete from import_guia_sadt igs
         where igs.val_seq_import = cg.num_seqcial;
      exception
        when others then
          null;
      end;
      vcont := vcont + 1;
    
    end if;
  
    for cm in c_movimentos(vseq_guia) loop
    
      delete from import_guia_movto im
       where im.progress_recid = cm.progress_recid;
    
      vcont := vcont + 1;
    
    end loop;
  
    delete from import_guia_histor igh
     where igh.val_seq_guia = cg.num_seqcial;
  
    vcont := vcont + 1;
  
    delete from import_guia ig where ig.num_seqcial = cg.num_seqcial;
  
    vcont := vcont + 1;
  
    if vcont >= 1000 then
      commit;
      vcont := 0;
    end if;
  
  end loop;

end p_apaga_guias;
/

