prompt ================================
prompt
CREATE OR REPLACE PROCEDURE UNICOOGPS."P_MARRETA_CPF" IS

  VCONT NUMBER := 0;

  VNRCPF PESSOA.NRCGC_CPF%TYPE;

  VAOEXISTE CHAR;

  CURSOR C_CPFS IS
    SELECT IB.PROGRESS_RECID
      FROM IMPORT_BNFCIAR IB
     WHERE F_VALIDADOR_CPF(IB.NR_CGC_CPF) = 'F'
       AND IND_SIT_IMPORT <> 'GE'
       AND IND_SIT_IMPORT ='ER';

BEGIN

  FOR X IN C_CPFS LOOP

    VNRCPF := PCK_GERA_CPF.GERA_CPF;

    BEGIN
      SELECT 'S'
        INTO VAOEXISTE
        FROM IMPORT_BNFCIAR IB
       WHERE IB.NR_CGC_CPF = VNRCPF;

    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        VAOEXISTE := 'N';

    END;

    IF VAOEXISTE = 'N' THEN
      UPDATE IMPORT_BNFCIAR IB
         SET IB.NR_CGC_CPF = VNRCPF, ib.cd_cpf = VNRCPF
       WHERE IB.PROGRESS_RECID = X.PROGRESS_RECID;

      VCONT := VCONT + 1;

      IF VCONT > 1000 THEN
        COMMIT;
        VCONT := 0;

      END IF;

    END IF;

  END LOOP;
  COMMIT;
END P_MARRETA_CPF;
/

