prompt
CREATE OR REPLACE PROCEDURE UNICOOGPS."P_MIGRA_COMPL_CONTRAT" IS

  vCont number := 0;

  CURSOR C_DADOS IS

    SELECT CR.PROGRESS_RECID,
           C.NRBANCO,
           lpad(C.NRAGENCIA,
                (select length(b.cod_format_agenc_bcia)
                   from ems5.banco b
                  where b.u##cod_banco = c.nrbanco),
                0) NRAGENCIA,
           lpad((C.NRCONTA_CORRENTE),
                (select length(b.cod_format_cta_corren)
                   from ems5.banco b
                  where b.u##cod_banco = c.nrbanco),
                0) NRCONTA_CORRENTE
      FROM CLIENTE C, GP.CONTRAT@DBL_TREINA_UNICOO CR
     WHERE CR.ID_PESSOA = C.NRREGISTRO_CLIENTE;

BEGIN

  FOR CD IN C_DADOS LOOP

    UPDATE GP.CONTRAT@DBL_TREINA_UNICOO CC
       SET CC.COD_BANCO    = CD.NRBANCO,
           CC.AGENCIA      = CD.NRAGENCIA,
           CC.CONTA_CORREN = CD.NRCONTA_CORRENTE
     WHERE CC.PROGRESS_RECID = CD.PROGRESS_RECID;

    VCONT := VCONT + 1;

    IF VCONT > 5000 THEN
      COMMIT;
      VCONT := 0;
    END IF;

  END LOOP;

  COMMIT;

END P_MIGRA_COMPL_CONTRAT;
/

