    SELECT A.PROGRESS_RECID,
           SUBSTR(TXOCORRENCIA, 0, 152) DS_OBSERVACAO_1,
           nvl(SUBSTR(TXOCORRENCIA, 153, 152), ' ') DS_OBSERVACAO_2,
           nvl(SUBSTR(TXOCORRENCIA, 305, 152), ' ') DS_OBSERVACAO_3
      FROM (SELECT U.PROGRESS_RECID,
                   to_char(wm_concat(us.txocorrencia)) TXOCORRENCIA
