             WHERE US.NRSEQUENCIAL_USUARIO = B.NUM_SEQCIAL_BNFCIAR
               AND U.CD_CARTEIRA_ANTIGA = B.CD_CARTEIRA_ANTIGA
            -- AND U.PROGRESS_RECID = 1777
             GROUP BY U.PROGRESS_RECID) A;

BEGIN

  FOR U IN C_TXMSG LOOP

    UPDATE GP.USUARIO@DBL_TREINA_UNICOO US
       SET US.DS_OBSERVACAO##1 = SUBSTR((US.DS_OBSERVACAO##1|| ' '|| U.DS_OBSERVACAO_1),0,152),
