        VTAMANHOLOGRADOURO := LENGTH(PNOLOGRADOURO) -
                              ((LENGTH(PNRIMOVEL) + length(nvl(pSEPARADOR_ENDERECO_NUMERO,vSEP_END_NUMERO_DEFAULT))) -
