                AND    ROWNUM = 1),
                0,
                'N',
                'S') AOVINCULO_EMPREGATICIO,

       NULL NRULT_MES_INSS,
       
       NULL NRRAMAL1,
       
       NULL NRRAMAL2,
       
       NVL(P.NRCGC_CPF, 0) NRCPF_CNPJ,
       
       --'S' AOREPRES_UNIDADE,
       DECODE(AA.CDPRESTADOR,PR.CDPRESTADOR,'S','N') AOREPRES_UNIDADE,
       
       
       '01' CDHOR_URGENCIA,
       
       --DECODE(P.TPPESSOA, 'F', 'S', 'N') AORECOLHE_INSS, -- MC
