       DECODE(PR.CDCLASSEIR,'00','N','S') AOCALCULA_IR,
       -- o que  o 03 ????!!!!
       '03' NRIND_IRRF, --01 sobre valor princ do proced - 02 sobre valor aux - 03 - ambos
       
