       'N' AOCALCULA_ADIANTAMENTO,
       PD.NRDEPENDENTE,
       'N' AOPAGTORH,
       nvl(E.CDEMAIL,e.cdemail_adicional) EMAIL,
       
       '           1' TPFLUXO, -- ser corrigido posteriormente no loop
       
       NULL CDIMPOSTO,
       NULL CDCLASSIF_IMPOSTO,
       P.NRPIS,
