       NULL CDIMPOSTO_PISPASEP,
       NULL CDIMPOSTO_CSLL,
       NULL CDCLASSIF_COFINS,
       NULL CDCLASSIF_PISPASEP,
       NULL CDCLASSIF_CSLL,
       NULL CDIMPOSTO_INSS,
       NULL CDCLASSIF_INSS,
       NULL AOCALCULA_ISS,
       NULL CDIMPOSTO_ISS,
       NULL CDCLASSIF_ISS,
       
       'N' AODEDUZ_ISS_UC,
       
       '30' PRZ_RETORNO,
       
