                 AND P1.CDPARAMETRO = 'DEPIRID1'
                 AND P2.CDPARAMETRO = 'DEPIRID2'
                 AND IDADE(P.DTNASCIMENTO, SYSDATE) <=
                     DECODE(PD.CDTIPO_DEPENDENTE,
