             DECODE(ENDER.CDVALOR_EXTERNO,1,0,2,2,3,1,4,3) TPENDERECO_END,
             ENDPRES.TPENDERECO TPENDERECO_UNICOO,
             --CAMPOS DA NOVA VERSO - ULTIMOS CAMPOS DO LAYOUT
             0 NRLEITOS_INTERMED_NEONATAL,
