                     vEndereco,
                     vEndereco_semComplemento,
                     vComplemento_Endereco,
                     RPRESTADOR.BAIRRO,
                     RPRESTADOR.NRCIDADE,
                     RPRESTADOR.CEP,
                     RPRESTADOR.CDESTADO
                FROM (SELECT P.NRREGISTRO_PREST NRREGISTRO,
                             E.TPENDERECO,
                             E.NOLOGRADOURO,
                             E.NRIMOVEL,
                             E.TXCOMPLEMENTO,
