                                       dat_livre_7, dat_livre_8, dat_livre_9, dat_livre_10, cdn_tipo_dep, 
                                       progress_recid)
                   Values (Rx.nrregistro_prest /*idi_pessoa*/, Rx.nrregistro_dependente /*cdd_pessoa_dep*/,
                           Rx.log_irrf, Rx.log_salario_familia,
                           ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', Null, /*cod_livre_1~10*/
