          vNecesEspeciais := 0;
          if ((Rx.log_def_fisica + Rx.log_def_visual + Rx.log_def_auditiva +
               Rx.log_def_mental + Rx.log_def_intelectual + Rx.log_reabil) > 0) then
            vNecesEspeciais := 1;
