    vnrsequencialorigem := pnrsequencial_origem;
    -- vnrcontrolemigracao := control_migrac_seq.nextval;

    insert into control_migrac
      (num_seqcial,
       u##ind_tip_migrac,
       ind_tip_migrac,
       dat_gerac,
       num_seqcial_orig,
       dt_processamento,
       ind_acao,
       des_observacao,
       u##ind_sit_import,
       ind_sit_import,
       u##identificacao,
       identificacao,
       dat_ult_atualiz,
       hra_ult_atualiz,
       cod_usuar_ult_atualiz,
       progress_recid)
    values
      (vnrcontrolemigracao, --num_seqcial,
       ptpintegracao, --u##ind_tip_migrac,
       ptpintegracao, --ind_tip_migrac,
       trunc(sysdate), --dat_gerac,
       vnrsequencialorigem, --num_seqcial_orig,
       trunc(sysdate), --dt_processamento,
       'I', --ind_acao,
       ' ', --des_observacao,
