       pcdidentificador, --u##identificacao,
       pcdidentificador, --identificacao,
       trunc(sysdate), --dat_ult_atualiz,
       to_char(sysdate, 'HH24:MM:SS'), --hra_ult_atualiz,
       'MIGRACAO', --cod_usuar_ult_atualiz,
