                           and f.nrcontrato = rproposta.nrcontrato
                           and f.tpfatura = '7'/*mensalidade*/
                           and f.aosituacao_fatura = '0'; --valida
                      end if;    
                 end if;
            end if;
          exception
            when others then vaa_mm_ult_fat := null;
          end;

