	                        from gp.contrat cc
	                       where cc.u##nome_abrev = vnom_abrev_cliente;
	                    exception
	                      when others then
	                        vind_si_import := 'CORIGEM';
	                        p_insere_falha(rproposta.nrsequencial_controle,
