	                                       sqlerrm || ';NOM_ABREV;' || vnom_abrev_cliente,
	                                       rproposta.contrato_antigo);
	                    end;
	                  end if;

	                  if vind_si_import = 'CORIGEM' and
	                     rproposta.tppessoa = 'F' then
	                    rproposta.nr_insc_contrat_origem := null;
	                    vind_si_import                   := 'RC';
