                  p_commit;

                  ---inicio integracao unicoo
                  --verifica se existe na tabela cliente

                  begin
                    select 'S'
                      into vaoexiste_cliente_unicoo
                      from producao.cliente@unicoo_homologa c
                     where nrregistro_cliente = rproposta.nrregistro
                       and rownum = 1;
