                      end;
                  end;
                  --fim
                  --inicio integracao ems5

                  begin
                    select 'S'
                      into vaoexiste_cliente_ems5
                      from ti_pessoa tp
                     where tp.nrregistro = rproposta.nrregistro;

                    vtxmensagem := vtxmensagem ||
