                                       ti_grupo_de_cliente              t
                                 where p.nrregistro = c.nrregistro_cliente
                                   and t.cdgrupo_cliente = c.cdgrupo_cliente
                                   and t.cdgrupo_cliente_ems_pj <> 'NAOM'
                                   and p.nrregistro = rproposta.nrregistro
