                          ems506unicoo.pck_ems506unicoo.p_define_ti_pessoa(x.nrcgc_cpf,
                                                                           x.nopessoa,
                                                                           x.noabreviado,
                                                                           x.dtnascimento,
                                                                           x.tppessoa,
                                                                           x.nrregistro_cliente,
                                                                           x.tipo,
                                                                           x.vnom_abrev_fornecedor,
                                                                           x.vcdn_fornecedor,
                                                                           x.vnom_abrev_cliente,
                                                                           x.vcdn_cliente,
                                                                           x.incluir);

                        exception
                          when others then
                            raise_application_error(-20100,
                                                    ' Pessoa : ' ||
                                                    x.nrregistro_cliente ||
                                                    ' - ' || sqlerrm);
                            null;
