                                                                                   x.nrregistro_cliente,
                                                                                   null,
                                                                                   null,
                                                                                   null);

                        --commit;

                        vtxmensagem := vtxmensagem ||
                                       ' Rodar API de integracao do cliente no ems5';

                      end loop;

                  end;

                  p_insere_falha(rproposta.nrsequencial_controle,
