--DBMS_OUTPUT.PUT_LINE( 'P11.21 dtexclusao(antes)/vdtfinal_aposdem: ' || rbeneficiario.dtexclusao || ';' || vdtfinal_aposdem);
          rbeneficiario.dtexclusao := vdtfinal_aposdem;

        -- EH TITULAR MAS NAO EH DEMITIDO/APOSENTADO          
        else
