                      case
                        when ib.dt_inclusao_plano > tcu.dtinclusao then
                          ib.dt_inclusao_plano
                        else
                          tcu.dtinclusao
                      end dt_inicio,
                      ib.dt_exclusao_plano,
                      0 lg_bonifica_penaliza,
                      0 nr_dias,
                      ib.aa_ult_fat_period aa_ult_fat,
                      ib.num_mes_ult_faturam num_mes_ult_faturam,
                      case
